var lxu = require("lxutils"); 
 

module.exports.get = function(req,res) { 
	var pathToJSON,pathindex,savepath;  
	pathindex = req.query.jfile||1;
	pathToJSON = req.app.locals.jfiles[pathindex]; 
	savepath = req.query.path; 
	var terse = (!(Boolean(req.query.terse)) || req.query.terse==="") ? 0 : req.query.terse;
	if(pathToJSON === "") { 
		res.render("log",{title:"Modifications Log",jfile:pathindex,caller:"/",terse:terse}); 
	} else { 
		lxu.jRender(pathToJSON,"log",{title:"Modifications Log",jfile:pathindex,caller:"/",path:savepath,terse:terse},res); 
	}
}; 